mapper.update:
UPDATE
         t_sys_right
SET
        pid=#{pid}
        ,right_name=#{rightName}
        ,show_order=#{showOrder}
        ,right_url=#{rightUrl}
        ,right_type=#{rightType}
        ,remark=#{remark}
        ,logo_url=#{logoUrl}
        ,is_enable=#{isEnable}

WHERE
        1=1
        AND right_id=#{rightId}

mapper.select:
SELECT
        right_id  as "rightId"
        ,pid
        ,right_name as "rightName"
        ,show_order as "showOrder"
        ,right_url as "rightUrl"
        ,right_type
        ,remark
        ,logo_url as "logoUrl"
        ,is_enable as "isEnable"

FROM
        t_sys_right
WHERE
         1=1
         AND right_id=#{rightId} 
         AND pid=#{pid} 
         AND right_name=#{rightName} 
         AND show_order=#{showOrder} 
         AND right_url=#{rightUrl} 
         AND right_type=#{rightType} 
         AND remark=#{remark} 
        order by show_order, right_id
